"""
    This module contains the decorators for the RoboGalaxyLibrary.
    Decorators are used to transform functions.  Please see Python
    documentation for more information.

"""
from robot.api import logger


def deprecated(use_instead):
    """ Deprecated decorator
        Use this to decorate your deprecated functions.
        A warning will be logged to the console.

        Example:
        @deprecated("myfunc")
        def my_deprecated_function():
        ...
        ...
    """
    def deprecated_inner(func):
        """ Inner wrapper for deprecated function """
        def wrapper(*args, **kwargs):
            """ wrapper for deprecated decorator
            """
            logger.warn("Deprecated function {0}.  Please use '{1}' instead.".format(func.__name__, use_instead))
            return func(*args, **kwargs)
        wrapper.__name__ = func.__name__
        wrapper.__doc__ = func.__doc__
        wrapper.__dict__.update(func.__dict__)
        return wrapper
    return deprecated_inner
