'''
API Library keywords
'''

from RoboGalaxyLibrary.api.common import HPCIManager
from RoboGalaxyLibrary.api.common import api_lib


class ApiLibKeywords(object):

    """Library for API Library.

    This library makes selected api_lib.py functions available as keywords.
    """

    ROBOT_LIBRARY_SCOPE = 'Global'

    def __init__(self):
        self.efusion_client = HPCIManager.HPCIManager()

    def validate_json_schema(self, data, schema):
        """validate json schema using jsonschema Draft3Validator
        Example:
        | ${bool value}=    |    Validate JSON Schema    |    data    |    schema    |
        """

        return api_lib.validate_json_schema(data, schema)
