from RoboGalaxyLibrary.configmgr import reset5900Switch


class ConfigMgrKeywords(object):

    '''
    classdocs
    '''

    ROBOT_LIBRARY_SCOPE = 'Global'

    def __init__(self):
        '''
        Constructor
        '''

    # Reset/setup switch with DL server

    def reset_5900_dl(self, dl_ip, dl_user='root', dl_pw='hpvse123'):
        reset5900Switch.reset_5900_dl(dl_ip, dl_user, dl_pw)

    # Reset/setup switch through serial console switch
    def reset_5900_direct(self, port, sw_ip, reboot=True, save=True):
        reset5900Switch.reset_5900_direct(port, sw_ip, reboot, save)
