from RoboGalaxyLibrary.ipdu import ipdu_client


class iPDUKeywords(object):

    '''
    classdocs
    '''

    def __init__(self):
        '''
        Constructor
        '''
        pass

    def send_ipdu_outlet_control(self, server, xml, port=50443):
        """
        This function send an outlet control to the iPDU using XML script to control
        the iPDU Intelligent Extension Bar outlets.
        Example:
        | Send iPDU Outlet Control | ${server} | ${xml} | port=${port} |
        """
        ipdu_client.ipdu_power_control(server, xml, port=port)
