from RoboGalaxyLibrary.utilitylib import logging as logger
from RoboGalaxyLibrary.cli.OA import oa_ssh


class OAKeywords(object):

    def __init__(self):
        pass

    def tbird_convert_oa_fan_status_to_dictionary(self, status):
        """
        This function returns Fan Bay Number.
        """
        return oa_ssh.convert_oa_fan_status_to_dictionary(status)

    def tbird_convert_oa_all_fan_status_to_dictionary(self, status):
        """
        This function returns fan information.
        Example:
        | Tbird Convert OA All Fan Status To Dictionary   |   ${status}    |
        """
        return oa_ssh.convert_oa_all_fan_status_to_dictionary(status)

    def tbird_get_fan_count(self, fan_dict, status_type):
        """
        Gets the count of fan filtered by status type
        Example:
        | `Tbird Get fan Count`         | ${fan_dict}    ${status_type}   |
        """
        return oa_ssh.get_fan_count(fan_dict, status_type)

    def tbird_convert_oa_coolingsubsystem_status_to_dictionary(self, status):
        """
        This function returns COOLING SUBSYSTEM information filtered by status.
        Example:
        | `Tbird Convert OA Coolingsubsystem Status To Dictionary       | ${status}   |
        """
        return oa_ssh.convert_oa_coolingsubsystem_status_to_dictionary(status)

    def tbird_convert_oa_fan_data_to_ui_format(
            self,
            oa_fan_dict,
            bay_num,
            fan_rule_list):
        """
        Convert FAN data from OA to UI format for easier comparison
        """
        return oa_ssh.convert_oa_fan_data_to_ui_format(
            oa_fan_dict,
            bay_num,
            fan_rule_list)

    def tbird_convert_oa_all_fan_data_to_ui_format(
            self,
            oa_dict,
            fan_rule_list):
        """
            Convert all Fan data from OA to UI format for easier comparison
        """
        return oa_ssh.convert_oa_all_fan_data_to_ui_format(
            oa_dict,
            fan_rule_list)

    def tbird_convert_oa_powersupply_status_to_dictionary(self, status):
        """
        This function returns POWER SUPPLY information.
        """
        return oa_ssh.convert_oa_powersupply_status_to_dictionary(status)

    def tbird_convert_oa_powersubsystem_status_to_dictionary(self, status):
        """
        This function returns POWER SUBSYSTEM information.
        """
        return oa_ssh.convert_oa_powersubsystem_status_to_dictionary(status)

    def tbird_convert_oa_all_powersupply_status_to_dictionary(self, status):
        """
        This function returns POWER SUPPLY information.
        """
        return oa_ssh.convert_oa_all_powersupply_status_to_dictionary(status)

    def tbird_convert_oa_snmp_status_to_dictionary(self, status):
        """
        This function returns SNMP information
        """
        return oa_ssh.convert_oa_snmp_status_to_dictionary(status)

    def tbird_get_powersupply_count(self, ps_dict, status_type):
        """
            Get the count of power supply filtered by type
        """
        return oa_ssh.get_powersupply_count(ps_dict, status_type)

    def tbird_convert_oa_powersupply_data_to_ui_format(self, oa_dict, bay_no):
        """
            Convert PS data from OA to UI format for easier comparison
        """
        return oa_ssh.convert_oa_powersupply_data_to_ui_format(oa_dict, bay_no)

    def tbird_convert_oa_all_powersupply_data_to_ui_format(self, oa_dict):
        """
            Convert all PS data from OA to UI format for easier comparison
        """
        return oa_ssh.convert_oa_all_powersupply_data_to_ui_format(oa_dict)
