""""Library version information"""
import sys

VERSION = '3.00'
RELEASE = ''
TIMESTAMP = '20161102-110000'


def get_version(sep=' '):
    """Get basic version information"""
    if RELEASE == 'beta':
        return VERSION
    return VERSION + sep + RELEASE


def get_full_version(who=''):
    """Get detailed version and platform information"""
    sys_version = sys.version.split()[0]
    version = '%s %s (%s %s on %s)' \
        % (who, get_version(), _get_interpreter(), sys_version, sys.platform)
    return version.strip()


def _get_interpreter():
    """Get Python interpreter type"""
    if sys.platform.startswith('java'):
        return 'Jython'
    if sys.platform == 'cli':
        return 'IronPython'
    if 'PyPy' in sys.version:
        return 'PyPy'
    return 'Python'
