scriptContent.content:
# (C) Copyright 2015-2015 Hewlett-Packard Development Company, L.P.
# Script to mount disk in libguesfs virtual appliance for personalizaiton or generalization
run
mount /dev/sda5 /


# creating tmp dir at local filesystem
!mkdir -p /tmp_dir/onetime
!mkdir -p /tmp_dir/state/local

# copying required files for personalization/generalization to local filesysystem
-copy-out /onetime.tgz /tmp_dir
-copy-out /state.tgz /tmp_dir
-copy-out /boot.cfg /tmp_dir

# trying to findout whether onetime.tgz will be picked up during booting or state.tgz will be picked
# based on that we can process either onetime.tgz or state.tgz for personalization/generalization

-!grep "onetime.tgz"  /tmp_dir/boot.cfg >/tmp_dir/onetime_found
-!grep "state.tgz" /tmp_dir/boot.cfg > /tmp_dir/state_found
# (C) Copyright 2015-2015 Hewlett-Packard Development Company, L.P.

# script to personalize an ESXi image


!if [ -s  "/tmp_dir/onetime_found" ];
 then
cd  /tmp_dir/onetime;
tar zxvf ../onetime.tgz;
echo esxcli system hostname set -H "@hostname:newhostname@">>   /tmp_dir/onetime/etc/rc.local.d/local.sh;
chmod 777   /tmp_dir/onetime/etc/rc.local.d/local.sh;
tar czvf ../onetime.tgz *;
else
cd /tmp_dir/state;
tar zxvf ../state.tgz;
cd /tmp_dir/state/local;
tar zxvf ../local.tgz;
echo esxcli system hostname set -H  "@hostname:newhostname@">>   /tmp_dir/state/local/etc/rc.local.d/local.sh;
chmod 777  /tmp_dir/state/local/etc/rc.local.d/local.sh;
tar czvf ../local.tgz *;
cd /tmp_dir/state;
tar czvf ../state.tgz local.tgz;
fi
# (C) Copyright 2015-2015 Hewlett-Packard Development Company, L.P.
# Script to unmount disk in libguesfs virtual appliance for personalizaiton or generalization

-copy-in /tmp_dir/onetime.tgz /
-copy-in  /tmp_dir/state.tgz /
umount /

